INSERT INTO `book` VALUES ('227', '所谓情商高，就是懂分寸', '9787516824146', '陈玉新', '台海出版社', 'http://img11.360buyimg.com/n1/jfs/t1/80719/2/11007/223254/5d88411aEfe13a90b/65840096c4c724ca.jpg', null, '励志与成功', '情商管理', '分寸感往往是体现一个人情商高低的分水岭。有分寸感的人，说话办事往往拿捏到位、恰到好处，与任何人都能愉快地相处。本书真实生动的案例和有力的评述告诉读者：情商高的人都是善于把握分寸的人。人生就像一场比赛，你可能遭遇无数对手，而ZUI强劲的对手却是你自己。所以说，胜己者，胜天下，只有把握好做人做事的分寸，你才有可能获得人生这场比赛的胜利。', '2');
